import React from "react";

const Area = ({ values }) => {
  const pi = 3.14;
  const shape = values.shape;
  const radius = values.diameter / 2;
  const side = values.side;
  const length = values.length;
  const breadth = values.breadth;
  const axisA = values.axisA;
  const axisB = values.axisB;

  let area = 0;

  if (shape === "") {
    return null;
  }

  console.log(values);
  console.log(shape);

  switch (shape) {
    case "circle":
      console.log(radius);
      area = pi * radius * radius;
      break;
    case "square":
      console.log(side);
      area = side * side;
      break;
    case "ellipse":
      console.log(axisA);
      console.log(axisB);
      area = pi * axisA * axisB;
      break;
    case "rectangle":
      console.log(length);
      console.log(breadth);
      area = length * breadth;
      break;
  }

  return (
    <div>
      <h5>
        <b>Step 3 - Your Results</b>
      </h5>
      <p className="areaText">
        You have calculated the area of {shape} to be {area}. Below is your
        result:
      </p>
      <p className="areaResult">The area is {area} sq. units</p>
    </div>
  );
};

export default Area;
