import React, { Component } from "react";

class AreaCalculatorForm extends Component {
  state = {
    step: 1,
    shape: "",
    diameter: 0,
    side: 0,
    length: 0,
    height: 0,
    width: 0
  };

  // Proceed to the next step if not the final step
  nextStep = () => {
    const { step } = this.state;
  };

  render() {
    return (
      <div className="card">
        <div className="card-title">Area Calculator</div>
        <div className="card-content">Form will come here</div>
        <div className="card-footer">Footer here if needed</div>
      </div>
    );
  }
}

export default AreaCalculatorForm;
