import React from "react";

const Area = ({ values }) => {
  const pi = 3.14;
  const shape = values.shape;
  const radius = values.diameter / 2;
  const side = values.side;
  const length = values.length;
  const breadth = values.breadth;
  const axisA = values.axisA;
  const axisB = values.axisB;
  let area = values.area ? values.area : 0;

  if (shape === "") {
    return null;
  }

  console.log(values);

  console.log(axisA);
  console.log(axisB);
  console.log(shape);

  switch (shape) {
    case "circle":
      console.log(radius);
      console.log(diameter);
      area = pi * radius * radius;

    case "square":
      console.log(side);
      area = side * side;

    case "ellipse":
      area = pi * axisA * axisB;

    case "rectangle":
      console.log(length);
      console.log(breadth);
      area = length * breadth;
  }

  return (
    <div>
      <div className="card">
        <div className="card-titlw">Area of {shape}</div>
        <div className="card-content">
          The area of the {shape} is {area} sq. units
        </div>
      </div>
    </div>
  );
};

export default Area;
