import React from "react";

const ShapeSelectionForm = ({ nextStep, handleChange, values }) => {
  console.log(values);
  return (
    <form action="#">
      <p>
        <label>
          <input
            name="shape"
            type="radio"
            checked={values.shape === "circle"}
            onChange={handleChange}
          />
          <span>Circle</span>
        </label>
      </p>
      <p>
        <label>
          <input
            name="shape"
            type="radio"
            checked={values.shape === "square"}
            onChange={handleChange}
          />
          <span>Square</span>
        </label>
      </p>
      <p>
        <label>
          <input
            name="shape"
            type="radio"
            checked={values.shape === "ellipse"}
            onChange={handleChange}
          />
          <span>Ellipse</span>
        </label>
      </p>
      <p>
        <label>
          <input
            name="shape"
            type="radio"
            checked={values.shape === "circle"}
            onChange={handleChange}
          />
          <span>Rectangle</span>
        </label>
      </p>
    </form>
  );
};

export default ShapeSelectionForm;
