import React from "react";

const Area = ({
  shape,
  diameter,
  length,
  breadth,
  side,
  axisA,
  axisB,
  area
}) => {
  const pi = 3.14;
  const radius = diameter / 2;

  if (shape === "") {
    return null;
  }

  switch (shape) {
    case "circle":
      area = diameter === 0 ? 0 : pi * radius * radius;

    case "square":
      area = side === 0 ? 0 : side * side;

    case "ellipse":
      area = pi * axisA * axisB;

    case "rectangle":
      area = length * breadth;
  }

  return (
    <div>
      <div className="card">
        <div className="card-titlw">Area of {shape}</div>
        <div className="card-content">
          The area of the {shape} is {area} sq. units
        </div>
      </div>
    </div>
  );
};

export default Area;
