import React from "react";
import NavButton from "./NavButton";

const ActionButtons = ({ step, nextStep, prevStep, startAgain }) => {
  let classesPrev = "";
  let classesNext = "";
  let classesStart = "";
  switch (step) {
    case 1:
      let classes = "btn btn-primary float-right";
      return (
        <div>
          <NavButton
            name="Next Step"
            classes={classes}
            clickAction={nextStep}
          />
        </div>
      );
    case 2:
      let classesNext = "btn btn-primary float-right";
      let classesPrev = "btn btn-primary float-left";
      return (
        <div>
          <NavButton
            name="Previous Step"
            classes={classesPrev}
            clickAction={prevStep}
          />
          <NavButton
            name="Next Step"
            classes={classesNext}
            clickAction={nextStep}
          />
        </div>
      );
    case 3:
      let prevClasses = "btn btn-primary float-left";
      let classesStart = "btn btn-primary float-right";
      return (
        <div>
          <NavButton
            name="Previous Step"
            classes={prevClasses}
            clickAction={prevStep}
          />
          <NavButton
            name="Start over again"
            classes={classesStart}
            clickAction={startAgain}
          />
        </div>
      );
  }

  return <div className="card-footer" />;
};

export default ActionButtons;
