import React from "react";

const Area = ({ values }) => {
  const pi = 3.14;

  let area = values.area ? values.area : 0;
  if (values.shape === "") {
    return null;
  }

  switch (values.shape) {
    case "circle":
      if (values.diameter === 0) {
        area = 0;
      }
  }
};

export default Area;
