import React from "react";
import NavButton from "./NavButton";

const ActionButtons = ({ step, nextStep, prevStep, startAgain }) => {
  let classesPrev = "btn btn-primary right";
  let classesNext = "btn btn-primary left";
  let classesStart = "btn btn-primary left";

  switch (step) {
    case 1:
      return (
        <div>
          <NavButton name="Next" classes={classesNext} clickAction={nextStep} />
        </div>
      );
    case 2:
      return (
        <div>
          <NavButton
            name="Cancel"
            classes={classesPrev}
            clickAction={prevStep}
          />
          <NavButton name="Next" classes={classesNext} clickAction={nextStep} />
        </div>
      );
    case 3:
      return (
        <div>
          <NavButton
            name="Cancel"
            classes={classesPrev}
            clickAction={prevStep}
          />
          <NavButton
            name="Start over again"
            classes={classesStart}
            clickAction={startAgain}
          />
        </div>
      );
  }

  return <div className="card-footer" />;
};

export default ActionButtons;
