import React, { Component } from "react";
import ShapeSelectionForm from "./ShapeSelectionForm";

class AreaCalculatorForm extends Component {
  state = {
    step: 1,
    shape: "",
    diameter: 0,
    side: 0,
    length: 0,
    height: 0,
    width: 0
  };

  // Proceed to the next step if not the final step
  nextStep = () => {
    const { step } = this.state;

    step = step < 3 ? step + 1 : step;

    this.setState({
      step: step
    });
  };

  // Go back to the previous step if not the first step
  prevStep = () => {
    const { step } = this.state;

    step = step > 1 ? step - 1 : step;

    this.setState({
      step: step
    });
  };

  // Handle change of the fields
  handleChange = e => {
    this.setState({
      [e.target.id]: e.target.value
    });
  };

  render() {
    const { step } = this.state;
    const { shape, diameter, side, length, height, width } = this.state;
    const values = { shape, diameter, side, length, height, width };

    switch (step) {
      case 1:
        return (
          <ShapeSelectionForm
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 2:
        return <div />;
      case 3:
        return <div />;
    }

    return (
      <div className="card">
        <div className="card-title">Area Calculator</div>
        <div className="card-content">Form will come here</div>
        <div className="card-footer">Footer here if needed</div>
      </div>
    );
  }
}

export default AreaCalculatorForm;
