import React from "react";
import NavButton from "./NavButton";

const ActionButtons = ({ step, nextStep, prevStep, startAgain }) => {
  let classesPrev = "btn btn-primary float-left";
  let classesNext,
    classesStart = "btn btn-primary float-right";

  switch (step) {
    case 1:
      return (
        <div>
          <NavButton
            name="Next Step"
            classes={classesNe}
            clickAction={nextStep}
          />
        </div>
      );
    case 2:
      return (
        <div>
          <NavButton
            name="Previous Step"
            classes={classesPrev}
            clickAction={prevStep}
          />
          <NavButton
            name="Next Step"
            classes={classesNext}
            clickAction={nextStep}
          />
        </div>
      );
    case 3:
      return (
        <div>
          <NavButton
            name="Previous Step"
            classes={classesPrev}
            clickAction={prevStep}
          />
          <NavButton
            name="Start over again"
            classes={classesStart}
            clickAction={startAgain}
          />
        </div>
      );
  }

  return <div className="card-footer" />;
};

export default ActionButtons;
