import React, { Component } from "react";
import CalculatorForm from "./CalculatorForm";
import ActionButtons from "./ActionButtons";

class Calculator extends Component {
  state = {
    currentStep: 1,
    shape: "",
    area: 0,
    // dimension: {
    //   circle: {
    //     diameter: 0
    //   },
    //   square: {
    //     side: 0
    //   },
    //   rectangle: {
    //     length: 0,
    //     breadth: 0
    //   },
    //   ellipse: {
    //     axisA: 0,
    //     axisB: 0
    //   }
    // }
    dimension: [],
    shapes: ["circle", "ellipse", "square", "rectangle"]
  };

  shapes = ["circle", "ellipse", "square", "rectangle"];

  setShape = shape => {
    this.setState({
      shape: shape
    });
  };

  setPrevStep = () => {
    let prevStep = this.state.currentStep > 1 ? this.state.currentStep - 1 : 1;

    this.setState({
      currentStep: prevStep
    });
  };

  setNextStep = () => {
    let nextStep = this.state.currentStep < 3 ? this.state.currentStep + 1 : 3;

    this.setState({
      currentStep: nextStep
    });
  };

  setStart = () => {
    this.setState({
      currentStep: 1
    });
  };

  render() {
    console.log(this.state);
    return (
      <div className="container">
        <div className="wrapper">
          Wrapper Container
          <CalculatorForm
            shapes={this.shapes}
            setShape={this.setShape}
            state={this.state}
          />
          <ActionButtons
            state={this.state}
            setPrevStep={this.setPrevStep}
            setNextStep={this.setNextStep}
            setStart={this.setStart}
          />
        </div>
      </div>
    );
  }
}

export default Calculator;
