import React, { Component } from "react";
import Header from "./Header";
import LeftBlock from "./LeftBlock";
import RightBlock from "./RightBlock";
import MiddleBlock from "./MiddleBlock";
import StepOne from "./StepOne";
import StepTwo from "./StepTwo";

class CalculatorForm extends Component {
  state = {
    currentStep: 1,
    shape: "",
    area: 0,
    dimensions: {
      circle: {
        diameter: 0
      },
      ellipse: {
        height: 0,
        width: 0
      },
      square: {
        side: 0
      },
      rectangle: {
        height: 0,
        width: 0
      }
    }
  };

  handlePrev = e => {
    if (this.state.currentStep > 1) {
      this.setState(...this.state, {
        currentStep: this.state.currentStep - 1
      });
    }
  };

  handleNext = e => {
    if (this.state.currentStep < 3) {
      this.setState(...this.state, {
        currentStep: this.state.currentStep + 1
      });
    }
  };

  render() {
    let c = "";

    if (this.state.currentStep === 2) {
      c = <StepTwo states={this.state} />;
    } else if (this.state.currentStep === 3) {
      c = <StepOne states={this.state} />;
    } else {
      c = <StepOne states={this.state} />;
    }

    return (
      <div className="container">
        <div className="wrapper">
          <div className="header">
            <Header />
          </div>
          <div className="row">
            <div className="col-lg-4">
              <LeftBlock />
            </div>
            <div className="col-lg-4">
              <MiddleBlock />
            </div>
            <div className="col-lg-4">
              <RightBlock />
            </div>
          </div>
          <div className="row">{c}</div>
          <div className="btn btn-primary left">
            <button type="button" name="Previous" onclick="this.handlePrev">
              Previous
            </button>
          </div>
          <div className="btn btn-primary right">
            <button type="button" name="Next" onclick="this.handleNext">
              Next
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default CalculatorForm;
