import React from "react";
import NavButton from "./NavButton";

const ActionButtons = ({ step, nextStep, prevStep, startAgain }) => {
  switch (step) {
    case 1:
      let classes = "btn btn-primary float-right";
      return (
        <div>
          <NavButton name="Next Step" classes={classes} nextStep={nextStep} />
        </div>
      );
    case 2:
      let classesNext = "btn btn-primary float-right";
      let classesPrev = "btn btn-primary float-right";
      return (
        <div>
          <NavButton
            name="Previous Step"
            classes={classesPrev}
            clickAction={prevStep}
          />
          <NavButton name="Next Step" classes={classesNext} action={nextStep} />
        </div>
      );
  }

  return <div className="card-footer" />;
};

export default ActionButtons;
