import React from "react";

const Area = ({ diameter, length, breadth, side, axisA, axisB }) => {
  const pi = 3.14;
  const radius = diameter / 2;

  let area = values.area ? values.area : 0;

  if (values.shape === "") {
    return null;
  }

  switch (values.shape) {
    case "circle":
      area = values.diameter === 0 ? 0 : pi * radius * radius;
  }
};

export default Area;
